mapper.select:
select * from (
	select rownum as rn, com_num, content, writer, reg, ref, re_level, re_step, is_delete, num
	from (
		select com_num, content, writer, reg, ref, re_level, re_step, is_delete, num
		from comments where num = #{num} and is_delete = 1
		order by ref desc, re_step asc
	)
	where rownum <= #{offset} + #{pageSize} 
)
where rn > #{offset}
